import React, { useEffect, useState } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';

import './style.css'
import axios from './axios';
import Sec01 from './components/Sec01';
import Sec02 from './components/Sec02';
import Sec03 from './components/Sec03';
import Sec04 from './components/Sec04';
import Sec05 from './components/Sec05';
import Sec06 from './components/Sec06';
import Sec07 from './components/Sec07';
import Sec08 from './components/Sec08';


const Main = () => {

    const [wedding, setWedding] = useState({
        year: '',
        month: '',
        date: '',
        day: { kor: '', eng: '' },
        hour: 0,
        min : 0,
        location: '',
        address: ''
    });
    const [groom, setGroom] = useState({
        name: '',
        dadName: '',
        momName: '',
        dadNumber: '',
        momNumber: '',
        account: '',
        dadAccount: '',
        kakaoPay: '',
        dadKaKaoPay: ''
    });
    const [bride, setBride] = useState({
        name: '',
        dadName: '',
        momName: '',
        dadNumber: '',
        momNumber: '',
        account: '',
        dadAccount: '',
        kakaoPay: '',
        dadKaKaoPay: ''
    });

    useEffect(() => {
        setWedding({
            year: '2024',
            month: '09',
            date: '28',
            day: { kor: '토', eng: 'SATURDAY' },
            hour: 12,
            location: '스카이웨딩컨벤션 3층 그랜드엘렌홀',
            address: '전라남도 무안군 삼향읍 남악로 162번길 110 '
        })
        setGroom({
            name: '황인준',
            dadName: '황선욱',
            momName: '백순진',
            dadNumber: '010-1234-5678',
            momNumber: '010-5678-1234',
            account: '은행명 123-456-789',
            dadAccount: '은행명 456-789-123',
            kakaoPay: 'https://link.kakaopay.com/_/W72DIa1',
            dadKaKaoPay: 'https://link.kakaopay.com/_/W72DIa1'
        });
        setBride({
            name: '양세영',
            dadName: '양승진',
            momName: '박정국',
            dadNumber: '010-1234-5678',
            momNumber: '010-5678-1234',
            account: '은행명 789-123-456',
            dadAccount: '은행명 123-456-789',
            kakaoPay: 'https://link.kakaopay.com/_/W72DIa1',
            dadKaKaoPay: 'https://link.kakaopay.com/_/W72DIa1'
        });

        const observer = new IntersectionObserver(e => {
            e.forEach((e) => {
                e.isIntersecting
                    ? (e.target.style.opacity = 1)
                    : (e.target.style.opacity = 0)
            })
        });

        const section = document.querySelectorAll('.section-container');
        section.forEach((sec) => {
            observer.observe(sec)
        })
    }, [])

    return (
        <div>
            <div className="wrap-container">
                <div className="main-container">
                    <div className="section-conainer">
                        <Sec01 value={{ wedding, groom, bride }} />
                        <Sec02 />
                        <Sec03 />
                        <Sec04 value={{ wedding, groom, bride }} />
                        <Sec05 value={{ wedding, groom, bride }} />
                        <Sec06 />
                        <Sec07 />
                        <Sec08 value={{ wedding, groom, bride }} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Main
