import React, { useState } from 'react';
import './Sec06.css'; // 적절한 CSS 파일 경로로 변경해야 합니다.

const Sec06 = () => {
  const [open, setOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const imgList = Array.from({ length: 15 }, (_, index) => index + 1);

  const openModal = (imageSrc) => {
    setSelectedImage(imageSrc);
    setOpen(true);
  };

  const closeModal = () => {
    setOpen(false);
    setTimeout(() => {
      setSelectedImage(null);
    }, 250); // fadeOut 애니메이션과 동기화를 위한 시간 설정
  };

  return (
    <div className='section-container'>
      <div className='margin2'></div>
      <span className='font2 color3 space2'>GALLERY</span>
      <div className='margin5'></div>
      <span className='font4 color2 non-bold'>갤러리</span>
      <div className='margin5'></div>

      <div className='gallery-container'>
        {imgList.map((item, index) => (
          <div key={index} className='gallery-item' onClick={() => openModal(index + 1)}>
            <img className='gallery-img' src={`./img/gallery/${index + 1}.jpg`} alt={`Gallery ${index + 1}`} />
          </div>
        ))}
        <div className={`modal-container ${open ? 'open' : 'close'}`} onClick={closeModal}>
          <div className='modal-content'>
            <img src={`./img/gallery/${selectedImage}.jpg`} width='360px' alt='Selected Image' />
          </div>
        </div>
      </div>
      <div className='margin2'></div>
    </div>
  );
};

export default Sec06;
