import React, { useEffect, useState } from 'react'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const Sec06 = () => {

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  // 모달 상태
  const [open, setOpen] = useState(false);

  const imgList = Array.from({ length: 15 }, (_, index) => index + 1);

  // 상태 관리를 위한 useState 훅 사용
  const [selectedImage, setSelectedImage] = useState(null);

  // 이미지를 클릭했을 때 모달을 열고 선택한 이미지를 설정하는 함수
  const openModal = (imageSrc) => {
    setSelectedImage(imageSrc);
    setOpen(true);
  };

  // 모달을 닫는 함수
  const closeModal = () => {
    setOpen(false);
    setTimeout(() => {
      setSelectedImage(null);
    }, 3000)
  };

  return (
    <div className='section-container'>
      <div className='margin2'></div>
      <span className='font2 color3 space2'>GALLERY</span>
      <div className='margin5'></div>
      <span className='font4 color2 non-bold'>갤러리</span>
      <div className='margin5'></div>
      <div className='gallery-container'>
        {imgList.map((item, index) => (
          <div key={index} className='gallery-item' onClick={() => {
            openModal(index + 1)
            handleShow()
          }}>
            <img className='gallery-img' src={`./img/gallery/${index + 1}.jpg`} />
          </div>
        ))}
        <Modal show={show} onHide={handleClose}>
          <div className={open ? "modal-container open" : "modal-container close"} onClick={closeModal}>
            <div className='modal-content'>
              <img src={`./img/gallery/${selectedImage}.jpg`} width='360px' />
            </div>
          </div>
        </Modal>
        {/* {open && (
          <div show={show} className={open ? "modal-container open" : "modal-container close"}
            onClick={closeModal}>
            <div className='modal-content'>
              <img src={`./img/gallery/${selectedImage}.jpg`} width='360px' />
            </div>
          </div>
        )} */}
      </div>
      <div className='margin2'></div>
    </div>
  )
}

export default Sec06
